import React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';

const inputFieldValues = [
    {
        fullWidth: "true",
        required: "true",
        autoFocus: "true", 
        margin: "normal" ,
        label: "Your Email", 
        id: "your-email" 
    },
    {
        fullWidth: "true",
        margin: "normal", 
        label: "Subject", 
        id: "fullWidth", 
    },
    {
        fullWidth: "true",
        required: "true",
        multiline: "true", 
        rows: "8",
        margin: "normal",
        label: "Message", 
        id: "fullWidth" 
    }
  ];

function Contact() {
    return (
        <Box
        sx={{
            width: 500,
            maxWidth: "100%",
        }}
        >
            {inputFieldValues.map((inputFieldValue, index) => {
                return (
                    <TextField 
                        key={index}
                        fullWidth={inputFieldValue.fullWidth}
                        required={inputFieldValue.required ?? false}
                        multiline={inputFieldValue.multiline ?? false}
                        rows={inputFieldValue.rows ?? 1} 
                        margin={inputFieldValue.margin}
                        label={inputFieldValue.label}
                        id={inputFieldValue.id}
                    />
                );
            })}
        </Box>
    );
}

export default Contact;


