import React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';

const inputFieldValues = [
    {
      name: "fullName",
      label: "Full Name",
      id: "my-name"
    },
    {
      name: "email",
      label: "Email",
      id: "my-email"
    },
    {
      name: "message",
      label: "Message",
      id: "my-message",
      multiline: true,
      rows: 10
    }
  ];

function Contact() {
    return (
        <Box
        sx={{
            width: 500,
            maxWidth: "100%",
        }}
        >
            <TextField 
                fullWidth="true"
                required="true"
                autoFocus="true" 
                margin="normal" 
                label="Your Email" 
                id="fullWidth" 
            />
            <TextField 
                fullWidth 
                margin="normal" 
                label="Subject" 
                id="fullWidth" 
            />
            <TextField 
                fullWidth 
                required="true"
                multiline="true" 
                minRows={8} 
                margin="normal" 
                label="Message" 
                id="fullWidth" 
            />
        </Box>
    );
}

export default Contact;


