import React from 'react';
import Box from '@mui/system/Box';
import Grid from '@mui/material/Grid';
import List from '@mui/material/List';
import ListItemText from '@mui/material/ListItemText';
import Paper from '@mui/material/Paper';
import Typography from '@mui/material/Typography';

function Section(props) {
    const infoList = props.infoList.map((infoItem) =>
        <ListItemText 
            primary={infoItem[0]} 
            secondary={infoItem[1]} 
            sx={{
                fontFamily: "Abel"
            }}
        />
    );

    return (
        <Paper variant="elevation" elevation={10} sx={{backgroundColor: "#F7F4EA", borderRadius: "10px"}} className="paper">
            <Grid container direction="row" alignItems="center" spacing={2} className="grid">
                <Grid item md={4} xs={6}>
                    <Box
                        component="img"
                        sx={{
                            width: props.imgWidth || 0.5,
                            maxWidth: { xs: 200, s: 250, md: 350 },
                            maxHeight: 350,
                            borderRadius: props.imgBorderRadius || 0
                        }}
                        src={props.img} 
                        alt={props.alt}
                    />
                </Grid>
                <Grid item md={8} xs={6}>
                    <Typography 
                        component="h1" 
                        sx={{
                            fontFamily: "Abel",
                            fontSize: "32px",
                            lineHeight: "48px",
                            fontWeight: "bold"
                        }}
                    >
                        {props.title}
                    </Typography>
                    <Typography 
                        component="h3" 
                        className="subtitle"
                        sx={{
                            fontFamily: "Abel",
                            fontSize: "28px",
                            lineHeight: "48px"
                        }}
                    >
                        {props.subtitle}
                    </Typography>
                    <List>
                        {infoList}
                    </List>
                </Grid>
            </Grid>
        </Paper>
    );

}

export default Section;
